import React, { useContext, useState } from "react";
import { useForm } from "react-hook-form";

const WorkshopForm = () => {
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="text" placeholder="Title" name="Title" ref={register} />
      <input type="text" placeholder="Speaker" name="Speaker" ref={register} />
      <input type="text" placeholder="Room" name="Room" ref={register} />
      <input
        type="text"
        placeholder="Room Manager"
        name="Room Manager"
        ref={register}
      />
      <input
        type="text"
        placeholder="Description"
        name="Description"
        ref={register}
      />
      <select name="room type" ref={register}>
        <option value="Banquet">Banquet</option>
        <option value="Classroom">Classroom</option>
      </select>
      <select name="capacity" ref={register}>
        <option value="5">5</option>
        <option value="10">10</option>
        <option value="15">15</option>
        <option value="20">20</option>
        <option value="25">25</option>
      </select>
      <select name="status" ref={register}>
        <option value="open">Open</option>
        <option value="close">Closed</option>
      </select>
      <input type="submit" />
    </form>
  );
};

export default WorkshopForm;
