import React, { createContext, useState, useEffect } from "react";
import axios from "axios";

export const WorkshopContext = createContext();
const WorkshopContextProvider = (props) => {
  const [workshops, setWorkshops] = useState([]);
  const [tempWorkshop, setTempWorkshop] = useState([]);

  const addTempWorkshop = (
    date,
    title,
    description,
    speaker,
    room,
    roomManager,
    roomType,
    capacity,
    status
  ) => {
    setTempWorkshop([
      ...tempWorkshop,
      {
        date,
        title,
        description,
        speaker,
        room,
        roomManager,
        roomType,
        capacity,
        status,
      },
    ]);
  };

  useEffect(() => {
    axios
      .get("dummyData.json")
      .then((response) => setWorkshops(response.data.workshops));
  }, []);

  return (
    <div>
      <WorkshopContext.Provider value={{ workshops, tempWorkshop }}>
        {props.children}
      </WorkshopContext.Provider>
    </div>
  );
};
export default WorkshopContextProvider;
