import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const WorkshopContext = createContext();

const WorkshopContextProvider = (props) => {

    const [ workshops, setWorkshops ] = useState([]);
    const [ workshopDates, setWorkshopDates ] = useState([]);

    useEffect(() => {
        axios.get('dummyData.json')
        .then(response => setWorkshops(response.data.workshops))
    }
    , []);

    function handleFilterDate(date) {
        axios.get('dummyData.json')
        .then(response => {
            if(date === 'All workshops') {
                setWorkshopDates(response.data.workshops)
                    return workshopDates
            } else {
                const filterdResult = workshopDates
                workshopDates.filter(workshopDate => {
                    return workshopDate.date === date
                })
                setWorkshopDates(filterdResult)
            }
        })
    }




return ( 
    <div>
       <WorkshopContext.Provider value={{workshops, handleFilterDate}}>
           {props.children}
       </WorkshopContext.Provider>
    </div>
)
}

export default WorkshopContextProvider;
