import React, { useContext, useState } from "react";
import { useForm } from "react-hook-form";
import { WorkshopContext } from "../../../Context/WorkshopContext";

const WorkshopForm = () => {
  const { addTempWorkshop, workshops } = useContext(WorkshopContext);
  const { register, handleSubmit, errors } = useForm();

  const onSubmit = (data) => {
    const roomTypeID = data.roomType === "classroom" ? 1 : 2;
    const newObject = {
      id: "1",
      title: data.title,
      status_open: data.status,
      date: data.date,
      description: data.description,
      room: [
        {
          id: "1",
          name: data.room,
          capacity: data.capacity,
          room_type: [
            {
              id: roomTypeID,
              type: data.roomType,
            },
          ],
        },
      ],
    };
    console.log(newObject);
    addTempWorkshop(data);
    console.log(workshops);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="date" placeholder="date" name="date" ref={register} />
      <input type="text" placeholder="Title" name="title" ref={register} />
      <input type="text" placeholder="Speaker" name="speaker" ref={register} />
      <input type="text" placeholder="Room" name="room" ref={register} />
      <input
        type="text"
        placeholder="Room Manager"
        name="roomManager"
        ref={register}
      />
      <input
        type="text"
        placeholder="Description"
        name="description"
        ref={register}
      />
      <select name="roomType" ref={register}>
        <option value="Banquet">Banquet</option>
        <option value="Classroom">Classroom</option>
      </select>
      <select name="capacity" ref={register}>
        <option value="5">5</option>
        <option value="10">10</option>
        <option value="15">15</option>
        <option value="20">20</option>
        <option value="25">25</option>
      </select>
      <select name="status" ref={register}>
        <option value="open">Open</option>
        <option value="close">Closed</option>
      </select>
      <input type="submit" value="Create" />
    </form>
  );
};

export default WorkshopForm;
