import React, { useContext } from "react";
import { WorkshopContext } from "../../../Context/WorkshopContext";
import { RoomContext } from "../../../Context/RoomContext";
import { useForm } from "react-hook-form";
import uuid from "react-uuid";

const TempWorkshopInfo = ({ tempWorkshop }) => {
  const {
    confirmWorkshop,
    editMode,
    tempHandleChange,
    toggleEditmode,

    editTempWorkshop,
  } = useContext(WorkshopContext);
  const { register, handleSubmit } = useForm();

  const onSubmit = (data) => {
    const newObject = {
      id: uuid(),
      title: data.title,
      date: data.date,
      speaker: data.speaker,
      room: data.room,
    };
    editTempWorkshop(newObject);
  };

  const { rooms } = useContext(RoomContext);
  return (
    <div>
      <fieldset>
        {!editMode && <button onClick={toggleEditmode}>Edit Workshop</button>}
        {!editMode && (
          <li>
            <div>{tempWorkshop.newObject.date}</div>
            <div>{tempWorkshop.newObject.title}</div>
            <div>{tempWorkshop.newObject.speaker}</div>
            <div>{tempWorkshop.newObject.room}</div>
            <button onClick={confirmWorkshop}>Confirm Workshop</button>
          </li>
        )}
        {editMode && (
          <form onSubmit={handleSubmit(onSubmit)}>
            <input
              type="date"
              placeholder="date"
              name="date"
              value={tempWorkshop.newObject.date}
              onChange={tempHandleChange}
              ref={register}
            />
            <input
              type="text"
              placeholder="Title"
              name="title"
              value={tempWorkshop.newObject.title}
              onChange={tempHandleChange}
              ref={register}
            />
            <input
              type="text"
              placeholder="Speaker"
              name="speaker"
              value={tempWorkshop.newObject.speaker}
              onChange={tempHandleChange}
              ref={register}
            />
            <select name="room" onChange={tempHandleChange} ref={register}>
              {rooms.map((room) => {
                return (
                  <option key={room.id} value={room.name}>
                    {room.name}
                  </option>
                );
              })}
            </select>
            <input type="submit" value="Edit" />
          </form>
        )}
      </fieldset>
    </div>
  );
};

export default TempWorkshopInfo;
