import React, { useContext, useState } from 'react';
import { NotificationContext } from '../../../Context/NotificationContext';

const TempNotification = ( { tempNotification } ) => {

    const { confirmNotification } = useContext(NotificationContext);
    const { deleteNotification } = useContext(NotificationContext);
    const { editNotification } = useContext(NotificationContext);
    const [ editToggle, setEditToggle ] = useState(false);
    const [ currentNotification, setCurrentNotification] = useState();

    const editMode = () => {
        setEditToggle(!editToggle);
    }

    const handleChange = (e) => {
        let name = e.target.name;
        let value = e.target.value;

        if (name === 'users_emails' || name === 'subject' || name === 'content' || name === 'date') {
            confirmNotification();
        } else {
            confirmNotification();
        }
    }

    return (
        <div>
            <li>
                <button onClick={() => editMode()}>Edit Notification</button>
                <div>{tempNotification.users_emails}</div>
                <div>{tempNotification.subject}</div>
                <div>{tempNotification.content}</div>
                <div>{tempNotification.date}</div>
                <button onClick={confirmNotification}>Confirm Notification</button>
                <button onClick={() => deleteNotification(tempNotification.id)}>Delete Notification</button>
            </li>
            {editToggle &&
                <form onSubmit={(e) => editNotification(e, tempNotification)}>
                    <input name="users_emails" value={tempNotification.users_emails} onChange={handleChange} />
                    <input name="subject" value={tempNotification.subject} onChange={handleChange} />
                    <input name="content" value={tempNotification.content} onChange={handleChange} />
                    <input name="date" value={tempNotification.date} onChange={handleChange} />                         
                    <button type="submit" className="save-button">Save Changes</button>
                </form>
            }
        </div>
    )
}

export default TempNotification;
